// Product.jsx
import React from "react";
import { useParams } from "react-router-dom";

const Product = ({ allCars }) => {
  const { id } = useParams();
  const car = allCars.find((car) => car.id.toString() === id);

  if (!car) {
    return <div>Автомобиль не найден</div>;
  }

  return (
    <div>
      <h2>{car.title}</h2>
      <img src={car.img} alt={car.title} />
      <p>Информация: {car.info}</p>
      <p>Цена: {car.price}</p>
      {/* Добавьте другие детали автомобиля, если необходимо */}
    </div>
  );
}

export default Product;
