import './App.css';
import ReactDOM from "react-dom";
import React, { useState, useEffect } from 'react'; // Импортируйте useState и useEffect
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Main from './Main';
import Nav from './components/Navigation';
import Footer from './components/Footer';
import Product from './components/Product';
import Favourites from './components/Favorites';
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Cars from './index.json'; // Импортируйте данные из JSON-файла

function App() {
  const [search, setSearch] = useState(''); // Используйте useState для управления поиском
  const [allCars, setAllCars] = useState([]); // Сохраните данные в состоянии
  const [filteredCars, setFilteredCars] = useState([]); // Состояние отфильтрованных автомобилей

  useEffect(() => {
    // Загрузка данных из JSON файла (или вашего API)
    setAllCars(Cars.cars);
  }, []);

  const handleSearch = (e) => {
    setSearch(e.target.value);
  }

  const notify = () => toast("Car added to favorites");

  return (
    <BrowserRouter>
      <ToastContainer />
      <Nav search={search} handleSearch={handleSearch} /> {/* Передайте search и handleSearch в компонент Nav */}
      <Footer />
      <Routes>
        <Route path='favorites' element={<Favourites />} />
        <Route path="/" element={<Main notify={notify} filteredCars={filteredCars} />} />
        <Route path="/product" element={<Product />} />
      </Routes>
    </BrowserRouter>
  );
}

ReactDOM.render(<App />, document.getElementById("root"));
export default App;
