  import './App.css';
import ReactDOM from "react-dom";
import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route } from "react-router-dom";
import Main from './Main';
import Nav from './components/Navigation';
import Footer from './components/Footer';
import Product from './components/Product';
import Favourites from './components/Favorites';
import { ToastContainer, toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import Cars from '.ю/index.json';

function App() {
  const [search, setSearch] = useState('');
  const [allCars, setAllCars] = useState([]);
  const [filteredCars, setFilteredCars] = useState([]);

  useEffect(() => {
    // Загрузка данных из JSON файла (или вашего API)
    setAllCars(Cars.cars);
  }, []);

  useEffect(() => {
    // Фильтрация данных при изменении поискового запроса
    setFilteredCars(
      allCars.filter((item) =>
        item.title.toLowerCase().includes(search.toLowerCase())
      )
    );
  }, [search, allCars]);

  const handleSearch = (e) => {
    setSearch(e.target.value);
  }

  const notify = () => toast("Car added to favorites");

  return (
    <div>
      <li>
        <input
          onChange={handleSearch}
          value={search}
          className="text-input"
          type='text'
          placeholder="Search cars"
        />
      </li>

      <BrowserRouter>
        <ToastContainer />
        <Nav
          filteredCars={filteredCars}
          search={search}
          handleSearch={handleSearch}
        />
        <Footer />
        <Routes>
          <Route path='favorites' element={<Favourites />} />
          <Route path="/" element={<Main notify={notify} filteredCars={filteredCars} />} />
          <Route path="/product" element={<Product />} />
        </Routes>
      </BrowserRouter>
    </div>
  );
}

ReactDOM.render(<App />, document.getElementById("root"));
export default App;
